var hexvalue;
var mbusRange=[];
var i;
var slaverange;
var dtype;
//https://www.modbustools.com/modbus.html#function15
//https://www.fernhillsoftware.com/help/drivers/modbus/modbus-protocol.html
//6: Single reg
//5: Single coil
//15: Multiple Coils
//16: Multiple Registers

slaverange=msg.payload[8]*256 + msg.payload[9];

var startpos;
if(msg.payload[7]==6 || msg.payload[7]==5) startpos=10;
if(msg.payload[7]==16 || msg.payload[7]==15) startpos=13;

//DS-Single or multiple registers to array of Signed INT -32767 to 32767
if((msg.payload[7]==6 || msg.payload[7]==16) && (slaverange>=0 && slaverange<=4499)){ 	//Message type 16
    for (i = startpos; i < msg.payload.length; i += 2){
        hexvalue=msg.payload[i]*256 + msg.payload[i+1];
        if(hexvalue>32767)
            hexvalue=hexvalue-65536;
        mbusRange.push(hexvalue);
        dtype="DS";
    }
}
//DD-Single or multiple registers to array of Double INT
if((msg.payload[7]==6 || msg.payload[7]==16) && (slaverange>=16384 && slaverange<=18383)){ 	//Message type 16
    for (i = startpos; i < msg.payload.length; i += 4){
        hexvalue = msg.payload[i+2]*16777216 + msg.payload[i+3]*65536 + msg.payload[i]*256 + msg.payload[i+1];
        if(hexvalue>2147483647)
            hexvalue=hexvalue-4294967296;
        mbusRange.push(hexvalue);
        dtype="DD";
    }
}
//TXT-Single or Multiple registers to string
if((msg.payload[7]==6 || msg.payload[7]==16) && (slaverange>=36864 && slaverange<=37363)){
    for (i = startpos; i < msg.payload.length; i += 2){
        hexvalue=msg.payload[i+1]; //charCode.toString(16);
        mbusRange.push(String.fromCharCode(hexvalue));
        hexvalue=msg.payload[i];
        mbusRange.push(String.fromCharCode(hexvalue));
        mbusRange=[mbusRange.join("")];
        dtype="TXT";
    }
}
//DF-Single or Multiple registers to Float wordswap=off
if((msg.payload[7]==6 || msg.payload[7]==16) && (slaverange>=28672 && slaverange<=29670)){
    for (i = startpos; i < msg.payload.length; i += 4){
        var hex=(msg.payload[i+2].toString(16).length<2) ? "0"+msg.payload[i+2].toString(16) : msg.payload[i+2].toString(16);
        hex+=(msg.payload[i+3].toString(16).length<2) ? "0"+msg.payload[i+3].toString(16) : msg.payload[i+3].toString(16);
        hex+=(msg.payload[i].toString(16).length<2) ? "0"+msg.payload[i].toString(16) : msg.payload[i].toString(16);
        hex+=(msg.payload[i+1].toString(16).length<2) ? "0"+msg.payload[i+1].toString(16) : msg.payload[i+1].toString(16);
        const bytes = new Uint8Array(4)
        for (let i = 0; i < 4; i++) {
            bytes[i] = parseInt(hex.substr(i * 2, 2), 16);
        }
        const view = new DataView(bytes.buffer);
        mbusRange.push(view.getFloat32(0, false));
        dtype="DF";
    }
}

if((msg.payload[7]==5 || msg.payload[7]==15) && (slaverange>=8192 && slaverange<=8463))
    dtype="Y";
if((msg.payload[7]==5 || msg.payload[7]==15) && (slaverange>=16384 && slaverange<=18383))
    dtype="C";

//C,Y-Single Coil to array of binary number
if(msg.payload[7]==5) {			//Message type 05
    hexvalue=0;				//default to 0
    if(msg.payload[startpos]==255)		//if bit is high, set to 1
        hexvalue=1;
    mbusRange.push(hexvalue);
}
//C,Y-Multiple Coils to array of binary numbers
if(msg.payload[7]==15){ 			//Message type 15
    mbusrange='';
    for (i = startpos; i < msg.payload.length; i += 1){
        hexvalue=msg.payload[i].toString(2).padStart(8, '0');
        mbusrange=hexvalue+mbusrange;
    }
    mbusRange=mbusrange.split("");
    mbusRange=mbusRange.map(Number);
    mbusRange.reverse();
    var coilCount=msg.payload[10]*256 + msg.payload[11];
    mbusRange=mbusRange.slice(0,coilCount);
}

msg.payload=mbusRange;
msg.dataType=dtype;
return msg;
